/* =====================================================
 * change this to the email you want the form to send to
 * ===================================================== */
$email_to = "tezhan@hotmail.com";
$email_subject = "TezhanTezcan.com";

if(isset($_POST['email']))
{

    function return_error($error)
    {
        echo $error;
        die();
    }

    // check for empty required fields
    if (!isset($_POST['name']) ||
        !isset($_POST['email']) ||
        !isset($_POST['message']))
    {
        return_error('Lütfen tüm alanları doldurun.');
    }

    // form field values
    $name = $_POST['name']; // required
    $email = $_POST['email']; // required
    $message = $_POST['message']; // required

    // form validation
    $error_message = "";

    // name
    $name_exp = "/^[a-z0-9 .\-]+$/i";
    if (!preg_match($name_exp,$name))
    {
        $this_error = 'Lütfen adınızı girin.';
        $error_message .= ($error_message == "") ? $this_error : "<br/>".$this_error;
    }        

    $email_exp = '/^[A-Za-z0-9._%-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,4}$/';
    if (!preg_match($email_exp,$email))
    {
        $this_error = 'Lütfen geçerli bir email girin.';
        $error_message .= ($error_message == "") ? $this_error : "<br/>".$this_error;
    } 

    // if there are validation errors
    if(strlen($error_message) > 0)
    {
        return_error($error_message);
    }

    // prepare email message
    $email_message = "www.tezhantezcan.com üzerinden gönderilen mesajın detayları aşağıdaki gibidir.\n\n";

    function clean_string($string)
    {
        $bad = array("content-type", "bcc:", "to:", "cc:", "href");
        return str_replace($bad, "", $string);
    }

    $email_message .= "Gönderen: ".clean_string($name)."\n";
    $email_message .= "E-mail: ".clean_string($email)."\n";
    $email_message .= "Mesajı: ".clean_string($message)."\n";

    // create email headers
    $headers = 'From: '.$email."\r\n".
    'Reply-To: '.$email."\r\n" .
    'X-Mailer: PHP/' . phpversion();
    if (@mail($email_to, $email_subject, $email_message, $headers))
    {
        echo 'Mesajınız başarıyla gönderildi.';
    }
    else 
    {
        echo 'Bir hata oluştu. Lütfen daha sonra deneyin.';
        die();        
    }
}
else
{
    echo 'Lütfen tüm alanları doldurun.';
    die();
}
?>